	generateCSS_findProps: function (cg) {
		var props = [];
		for (i in cg.colorLevels) {
			for (p in cg.colorLevels[i]) {
				props[p] = cg.colorLevels[0][p];
			}
		}
		return props;
	},

	generateCSS_findNeighbour: function (prop, x, cg, what) {
		switch (what) {
			case 'above':
				for (p in cg.colorLevels) {
					if (cg.colorLevels[p][prop]) {
						if (p > x) {
							break;
						} else {
							var last = p;
						}
					}
				}
				if (last) return last;
				break;
			case 'beneath':
				for (p in cg.colorLevels) {
					if (cg.colorLevels[p][prop]) {
						if (p > x) {
							var last = p;
							break;
						} else {
							var last = p;
						}
					}
				}
				if (last) return last;
				break;
			case 'target':
				var sd = 101;
				var r = null;
				for (p in cg.colorLevels) {
					if (cg.colorLevels[p][prop]) {
						var diff = Math.abs(p - x);
						if (diff < sd) {
							sd = diff;
							r = p;
						}
					}
				}
				return r;
				break;
		}
		return false;
	},

	generateCSS_extractColor: function (combinedColor, what) {
		if (combinedColor.substr(0, 1) != '#') {
			var c = na.cg.getColorValue(combinedColor);
			if (!c) {
				na.hms.log('generateCSS_extractColor: Cannot translate color "' + combinedColor + '", using white.');
				combinedColor = '#ffffff';
			} else {
				combinedColor = c;
			}
		}
		switch (what) {
			case 'red':
				var r = ((na.cg.hex2dec(combinedColor.substr(1, combinedColor.length - 1)) & na.cg.hex2dec('ff0000')) >> 16);
				break;
			case 'green':
				var r = ((na.cg.hex2dec(combinedColor.substr(1, combinedColor.length - 1)) & na.cg.hex2dec('00ff00')) >> 8);
				break;
			case 'blue':
				var r = ((na.cg.hex2dec(combinedColor.substr(1, combinedColor.length - 1)) & na.cg.hex2dec('0000ff')));
				break;
		};
		return r;
	},

	getColorValue: function (x) {
		if (x.substr(0, 1) == '#') {} else {
			for (c in na.cg.globals.colorList) {
				if (c.toLowerCase() == x.toLowerCase()) return na.cg.globals.colorList[c];
			}
		}
		return false;
	},

	generateCSS_calculateColor: function (x, above, target, beneath) {
		//above, target & beneath here, are integers 0-255
		//returns the color also in int 0-255
		var r = ((above < beneath ? Math.round(above + ((above - beneath) * x) / 100) : Math.round(above - ((above - beneath) * x) / 100)));
		var r = Math.round(above - ((above - beneath) * x) / 100);
		return r;
	},

	generateCSS_calculateFloat: function (x, above, target, beneath) {
		//above, target & beneath here, are integers 0-255
		//returns the color also in int 0-255
		var r = ((above < beneath ? (above + ((above - beneath) * x) / 100) : (above - ((above - beneath) * x) / 100)));
		var r1 = (above - ((above - beneath) * x) / 100);
		return r1;
	},

	generateCSS_combineColor: function (ncd) {
		if (typeof ncd.red != 'number') na.hms.error('generateCSS_combineColor: invalid red ' + ncd.red);
		if (typeof ncd.green != 'number') na.hms.error('generateCSS_combineColor: invalid green ' + ncd.green);
		if (typeof ncd.blue != 'number') na.hms.error('generateCSS_combineColor: invalid blue ' + ncd.blue);
		
		var r = '#' + na.cg.dec2hex(ncd.red) + na.cg.dec2hex(ncd.green) + na.cg.dec2hex(ncd.blue);
		return r;
	},
	
	



